import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../images/logoimage.png';
import './backGround.module.css';
import axios from 'axios';

function MainBackgorund(props) {
  const navigate = useNavigate();
  const [loggedUserName, setLoggedUserName] = useState('지금 로그인하세요!');
  const [webtoontitle, setWebtoontitle] = useState('');

  return (
    <div className="mainPageBack">
      <div className="logoSearch">
        {' '}
        {/* 좌우구분용 네브 */}
        <div className="mainLogo">
          <button
            id="main_page"
            onClick={() => {
              navigate('/main_page');
            }}
          >
            <img src={logo} width="84px" height="93px" alt="image error" />
            <h1>Toonder</h1>
          </button>
        </div>
        <div className="mainSearch">
        <input
          id="mainSearchInput"
          type="text"
          placeholder="찾고싶은 웹툰 검색!"
          value={webtoontitle} // 현재 상태 값을 입력 값으로 설정
          onChange={(e) => setWebtoontitle(e.target.value)} // 입력 값이 변경될 때마다 상태 값을 업데이트
        />
        <button
          id="mainSearchButton"
          onClick={() => {
            navigate(`/search?title=${webtoontitle}`); // 웹툰 검색 페이지로 이동하면서 검색어를 쿼리 문자열로 전달
          }}
        >
          검색
        </button>
        </div>
      </div>
      {props.children} {/* props.children 렌더링 */}
    </div>

  );
}
export default MainBackgorund;
