import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../images/logoimage.png';
import './backGround.module.css';
import axios from 'axios';
import Search from '../main/search';

function MainBackgorund(props) {
  const navigate = useNavigate();
  const [loggedUserName, setLoggedUserName] = useState('지금 로그인하세요!');
  const [webtoontitle, setWebtoontitle] = useState('');

  const handleSearch = () => {
    navigate(`/search?title=${webtoontitle}`);
  };

  const listCreator = () => {
    var countForTrSplit = 1;
    var trWebtoonList = [];

    return (
      <>
        {firstLoading ? (
          <div id ='firstLoading'>
            <FaSpinner className="loadingIcon" />
          </div>
        ) : (
          <>
            <table>
              {webtoonList
                .filter((webtoonInfo) => webtoonInfo.title.includes(searchTitle))
                .map((webtoonInfo) => {
                  if (countForTrSplit === 1) {
                    trWebtoonList = [];
                  }

                  trWebtoonList.push(webtoonInfo);
                  if (countForTrSplit === 4) {
                    countForTrSplit = 1;
                    return (
                      <tr>
                        {trWebtoonList.map((trWebtoonInfo) => (
                          <td>
                            <tr>
                              <td>
                                <button
                                  onClick={() => {
                                    navigate('/mainwebtooninfo', { state: { mastrId: trWebtoonInfo.mastrId } });
                                  }}
                                >
                                  <img src={trWebtoonInfo.imageDownloadUrl} alt="image error" />
                                </button>
                              </td>
                            </tr>
                            <tr style={{ height: '65px' }}>
                              <td style={{ height: '75px' }}>
                                <p className="webtoonTitle">
                                  {trWebtoonInfo.title}
                                </p>
                              </td>
                            </tr>
                          </td>
                        ))}
                      </tr>
                    );
                  } else {
                    countForTrSplit += 1;
                  }
                })}
            </table>

            {isLoading ? ( // 로딩 중일 때의 화면
              <div id = 'isLoading'>
                <FaSpinner className="loadingIcon" />
              </div>
            ) : (
              <div></div>
            )}
          </>
        )}
      </>
    );
  };

  return (
    <div className="mainPageBack">
      <div className="logoSearch">
        <div className="mainLogo">
          <button
            id="main_page"
            onClick={() => {
              navigate('/main_page');
            }}
          >
            <img src={logo} width="84px" height="93px" alt="image error" />
            <h1>Toonder</h1>
          </button>
        </div>
        <div className="mainSearch">
          <input
            id="mainSearchInput"
            type="text"
            placeholder="찾고싶은 웹툰 검색!"
            value={webtoontitle}
            onChange={(e) => setWebtoontitle(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                handleSearch();
              }
            }}
          />
          <button id="mainSearchButton" onClick={handleSearch}>
            검색
          </button>
        </div>
      </div>
      {props.children}
    </div>
  );
}

export default MainBackgorund;
