import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './mainPage.css';
import axios from 'axios';
import MainBackgorund from '../backgrounds/mainBackground';
import toTop from '../../images/toTop.png';
import MainBackSmall from '../backgrounds/mainBackSmall';
import { FaSpinner } from 'react-icons/fa';

function Search(props) {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const initialSearchTitle = queryParams.get('title');

  const [webtoonList, setWebToonList] = useState([]);
  const [countPage, setCountPage] = useState(1);
  const [firstLoading, setFirstLoading] = useState(true);
  const [isLoading, setIsLoading] = useState(true);
  const [showNavigationToScrollTop, setShowNavigationToScrollTop] = useState(false);
  const [searchTitle, setSearchTitle] = useState(initialSearchTitle || '');

  const navigate = useNavigate();

  function onScroll() {
    const windowHeight = window.innerHeight || document.documentElement.clientHeight;
    const documentHeight = document.documentElement.scrollHeight;
    const scrolledToBottom = Math.ceil(window.scrollY + windowHeight) >= documentHeight;

    if (window.scrollY === 0) {
      setShowNavigationToScrollTop(false);
    } else {
      setShowNavigationToScrollTop(true);
    }

    if (scrolledToBottom) {
      setIsLoading(true);
      setCountPage((prevCount) => prevCount + 1);
    }
  }

  useEffect(() => {
    if (!isLoading) {
      window.addEventListener('scroll', onScroll);
      return () => {
        window.removeEventListener('scroll', onScroll);
      };
    }
  }, [isLoading]);

  useEffect(() => {
    console.log(props.webtoontitle);
  }, [props.webtoontitle]);

  useEffect(() => {
    axios.get(`toonder/webtoon?p_num=${countPage}&title=${searchTitle}`).then((res) => {
      if (webtoonList.length === 0) {
        setIsLoading(false);
        setWebToonList(res.data);
        setFirstLoading(false);
      } else {
        setIsLoading(false);
        setWebToonList((prevList) => prevList.concat(res.data));
      }
      console.log(res.title);
    });
  }, [countPage, searchTitle]);

  const handleSearch = (event) => {
    event.preventDefault();
    navigate(`/search?title=${searchTitle}`);
  };

  const listCreator = () => {
    var countForTrSplit = 1;
    var trWebtoonList = [];

    return (
      <>
        {firstLoading ? (
          <div id="firstLoading">
            <FaSpinner className="loadingIcon" />
          </div>
        ) : (
          <>
            <table>
              {webtoonList.map((webtoonInfo, index) => {
                if (index % 4 === 0) {
                  trWebtoonList = [];
                }
                trWebtoonList.push(webtoonInfo);
                if (index % 4 === 3 || index === webtoonList.length - 1) {
                  return (
                    <tr>
                      {trWebtoonList.map((trWebtoonInfo) => (
                        <td>
                          <tr>
                            <td>
                              <button
                                onClick={() => {
                                  navigate('/mainwebtooninfo', { state: { mastrId: trWebtoonInfo.mastrId } });
                                }}
                              >
                                <img src={trWebtoonInfo.imageDownloadUrl} alt="image error" />
                              </button>
                            </td>
                          </tr>
                          <tr style={{ height: '65px' }}>
                            <td style={{ height: '75px' }}>
                              <p className="webtoonTitle">{trWebtoonInfo.title}</p>
                            </td>
                          </tr>
                        </td>
                      ))}
                    </tr>
                  );
                }
              })}
            </table>

            {isLoading ? (
              <div id="isLoading">
                <FaSpinner className="loadingIcon" />
              </div>
            ) : (
              <div></div>
            )}
          </>
        )}
      </>
    );
  };

  return (
    <>
      <MainBackgorund>
        <MainBackSmall></MainBackSmall>

        <div className="webSearch">        <form onSubmit={handleSearch}>
          <input
            type="text"
            value={searchTitle}
            onChange={(event) => setSearchTitle(event.target.value)}
            placeholder="검색어를 입력하세요."
          />
          <button type="submit">검색</button>
        </form>{listCreator()}</div>

        {showNavigationToScrollTop ? (
          <div
            id="showNavigationToScrollTop"
            onClick={() => {
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
          >
            <img src={toTop} alt="To Top" style={{ height: '100%', width: '100%' }} />
          </div>
        ) : null}
      </MainBackgorund>
    </>
  );
}

export default Search;
