const listCreator = () => {
  var countForTrSplit = 1;
  var trWebtoonList = [];

  return (
    <>
      {firstLoading ? (
        <div id="firstLoading">
          <FaSpinner className="loadingIcon" />
        </div>
      ) : (
        <>
          <table>
            {webtoonList
              .filter(
                (webtoonInfo) =>
                  webtoonInfo.title.includes(searchTitle) || webtoonInfo.title.includes(searchTitle)
              )
              .map((webtoonInfo, index) => {
                if (countForTrSplit === 1) {
                  trWebtoonList = [];
                }
                trWebtoonList.push(webtoonInfo);
                if (countForTrSplit === 4 || index === webtoonList.length - 1) {
                  countForTrSplit = 1;
                  return (
                    <tr>
                      {trWebtoonList.map((trWebtoonInfo) => (
                        <td>
                          <tr>
                            <td>
                              <button
                                onClick={() => {
                                  navigate('/mainwebtooninfo', { state: { mastrId: trWebtoonInfo.mastrId } });
                                }}
                              >
                                <img src={trWebtoonInfo.imageDownloadUrl} alt="image error" />
                              </button>
                            </td>
                          </tr>
                          <tr style={{ height: '65px' }}>
                            <td style={{ height: '75px' }}>
                              <p className="webtoonTitle">{trWebtoonInfo.title}</p>
                            </td>
                          </tr>
                        </td>
                      ))}
                    </tr>
                  );
                } else {
                  countForTrSplit += 1;
                }
              })}
          </table>

          {isLoading ? (
            <div id="isLoading">
              <FaSpinner className="loadingIcon" />
            </div>
          ) : (
            <div></div>
          )}
        </>
      )}
    </>
  );
};
