import React, { useState, useEffect, useRef } from 'react';
import Background from '../backgrounds/backGround';
import supabase from '../supabase';
import styles from './join.module.css';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const CheckboxContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  max-height: 300px;
  overflow-y: scroll;
  border: 1px solid #ccc;
  padding: 10px;
  width: 200px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  width: 50%;
  margin-bottom: 5px;
`;

const CheckboxInput = styled.input`
  margin-right: 5px;
`;

function Join() {
  useEffect(() => {
    document.title = 'Toonder 회원가입';
  }, []);
  const inputRef = useRef(null);
  const [pw, setPw] = useState(''); //비밀번호 값
  const [pwc, setPwc] = useState(''); //비밀번호 확인 값
  const [isPwCheck, setIsPwCheck] = useState(false); //비밀번호 확인 여부
  const [isPwValid, setIsPwValid] = useState(false); //비밀번호 유효성 여부
  const [firstName, setFirstName] = useState(''); //이름 값
  const [lastName, setLastName] = useState(''); //성씨 값
  const [email, setEmail] = useState(''); //이메일 값
  const [notAllow, setNotAllow] = useState(true); //회원가입 버튼 활성화 여부
  const [isValidEmail, setEmailValid] = useState(false); //이메일 유효성 여부
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [selectedHashtags, setSelectedHashtags] = useState([]);

  const handleCheckboxChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedHashtags((prevSelectedHashtags) => [
        ...prevSelectedHashtags,
        value,
      ]);
    } else {
      setSelectedHashtags((prevSelectedHashtags) =>
        prevSelectedHashtags.filter((hashtag) => hashtag !== value)
      );
    }
  };

  const hashtagOptions = [
    '공포',
    '드라마',
    '코믹',
    '일상',
    '판타지',
    '액션',
    '역사',
    '학원',
    'SF',
    '학습만화',
    '캠페인',
    '스포츠',
    '동성애',
    '추리',
    '모험',
    '무협',
    '시사',
    '교양',
    '요리',
    '성인',
    '순정',
    'BL',
    '소년',
    '미스터리',
    'GL',
    '로맨스판타지',
    '카툰',
    '기관발행물',
    '만화이론',
    '로맨스',
    '그래픽노블',
    '개그',
  ];

  const onKeyPress = (e) => {
    e.preventDefault();

    if (e.target.value.length !== 0 && e.key === 'Enter') {
      const tagValue = e.target.value.trim();
      if (whitelist.includes(tagValue) && !tagList.includes(tagValue)) {
        // whitelist에 지정된 단어들만 입력 가능하도록 검사
        submitTagItem();
      } else {
        e.target.value = '';
      }
    }
  };

  const submitTagItem = () => {
    let updatedTagList = [...tagList];
    updatedTagList.push(tagItem);
    setTagList(updatedTagList);
    setTagItem('');
  };

  const deleteTagItem = (e) => {
    e.preventDefault();
    const deleteTagItem = e.target.parentElement.firstChild.innerText;
    const filteredTagList = tagList.filter(
      (tagItem) => tagItem !== deleteTagItem
    );
    setTagList(filteredTagList);
  };
  useEffect(() => {
    if (
      isValidEmail &&
      isPwCheck &&
      isPwValid &&
      firstName.length > 0 &&
      lastName.length > 0
    )
      setNotAllow(false);
    else setNotAllow(true);
    return;
  }, [isValidEmail, isPwValid, isPwCheck, firstName, lastName]);

  const handleFirstName = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastName = (e) => {
    setLastName(e.target.value);
  };

  const handlePW = (e) => {
    setPw(e.target.value);

    const regex = /^(?=.*\d)(?=.*[a-zA-Z])[0-9a-zA-Z]{8,10}$/;
    if (regex.test(pw)) {
      setIsPwValid(true);
    } else {
      setIsPwValid(false);
    }

    if (e.target.value === pwc) {
      setIsPwCheck(true);
    } else {
      setIsPwCheck(false);
    }
  };

  const handleEmail = (e) => {
    setEmail(e.target.value);
    const regex =
      /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
    if (regex.test(email)) {
      setEmailValid(true);
    } else {
      setEmailValid(false);
    }
  };

  const handleCheckPw = (e) => {
    setPwc(e.target.value);

    if (pw === e.target.value) {
      setIsPwCheck(true);
    } else {
      setIsPwCheck(false);
    }
  };

  const handleSubmit = async (e) => {
    setLoading(true);
    axios
      .post('api/member/insert', {
        mem_id: email,
        mem_name: firstName + lastName,
        mem_hashtag: '',
      })
      .catch(function () {
        console.log('Error for sending user data to Spring - creating member');
      });

    e.preventDefault();

    const { data, error } = await supabase.auth.signUp({
      email: email,
      password: pw,
    });

    if (error) {
      alert(error);
      setLoading(false);
    } else alert('인증 메일을 발송했습니다. 이메일 확인 후 로그인해주세요.');
    navigate('/');
  };

  return (
    <Background text="Join" backgroundSize="600px 500px">
      {loading ? (
        <div className={styles.Loading}>잠시만 기다려주세요...</div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div className={styles.name}>
            <input
              type="text"
              onChange={handleFirstName}
              id="firstName"
              placeholder="이름"
            />
            <input
              type="text"
              onChange={handleLastName}
              id="lastName"
              placeholder="성"
            />
          </div>
          <div className={styles.email}>
            {!isValidEmail && email.length > 0 && (
              <div
                style={{
                  position: 'absolute',
                  top: '-20px',
                  color: 'white',
                  fontSize: '15px',
                }}
              >
                올바른 이메일을 입력해주세요
              </div>
            )}
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmail}
              placeholder="이메일 입력"
            />
          </div>

          <div className={styles.password}>
            {!isPwValid && pw.length > 0 && (
              <div
                style={{
                  position: 'absolute',
                  top: '-20px',
                  color: 'white',
                  fontSize: '15px',
                }}
              >
                영문, 숫자 포함 8~10자 이상 입력해주세요
              </div>
            )}
            <input
              type="password"
              onChange={handlePW}
              value={pw}
              placeholder="비밀번호"
            />
            {!isPwCheck && pw.length > 0 && (
              <div
                style={{
                  position: 'absolute',
                  top: '-20px',
                  color: 'white',
                  fontSize: '15px',
                  left: '270px',
                  fontWeight: 'normal',
                }}
              >
                비밀번호를 한번 더 정확히 입력해주세요
              </div>
            )}
            <input
              type="password"
              onChange={handleCheckPw}
              value={pwc}
              placeholder="비밀번호 확인"
            />
          </div>

          <div className={styles.textBox}>
            <CheckboxContainer>
              {hashtagOptions.map((hashtag) => (
                <CheckboxLabel key={hashtag}>
                  <CheckboxInput
                    type="checkbox"
                    value={hashtag}
                    onChange={handleCheckboxChange}
                  />
                  {hashtag}
                </CheckboxLabel>
              ))}
            </CheckboxContainer>
          </div>
          <div>
            <button className={styles.submit} disabled={notAllow} type="submit">
              <strong>Join</strong>
            </button>
          </div>
        </form>
      )}
    </Background>
  );
}
