import React, { useRef, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './mainPage.css';
import axios from 'axios';
import MainBackgorund from '../backgrounds/mainBackground';
import toTop from '../../images/toTop.png';
import MainBackSmall from '../backgrounds/mainBackSmall';
import { FaSpinner } from 'react-icons/fa'; // 로딩 아이콘 추가

function Search(props) {
  const [webtoonList, setWebToonList] = useState([]);
  const [countPage, setCountPage] = useState(1);
  const [firstLoading, setFirstLoading] = useState(true);
  const [isLoading, setIsLoading] = useState(true);
  const [showNavigationToScrollTop, setShowNavigationToScrollTop] = useState(false);
  const [searchTitle, setSearchTitle] = useState('');
  const [webtoontitle, setWebtoontitle] = useState('');

  const handleSearch = () => {
    navigate(`/search?title=${webtoontitle}`);
  };

  const navigate = useNavigate();

  function onScroll() {
    const windowHeight = window.innerHeight || document.documentElement.clientHeight;
    const documentHeight = document.documentElement.scrollHeight;
    const scrolledToBottom = Math.ceil(window.scrollY + windowHeight) >= documentHeight;

    if (window.scrollY === 0) {
      setShowNavigationToScrollTop(false);
    } else {
      setShowNavigationToScrollTop(true);
    }

    if (scrolledToBottom) {
      setIsLoading(true);
      setCountPage((prevCount) => prevCount + 1);
    }
  }

  useEffect(() => {
    if (!isLoading) {
      window.addEventListener('scroll', onScroll);
      return () => {
        window.removeEventListener('scroll', onScroll);
      };
    }
  }, [isLoading]);

  useEffect(() => {
    axios.get(`toonder/webtoon?p_num=${countPage}`).then((res) => {
      if (webtoonList.length === 0) {
        setIsLoading(false);
        setWebToonList(res.data);
        setFirstLoading(false);
      } else {
        setIsLoading(false);
        setWebToonList((prevList) => prevList.concat(res.data));
      }
    });
  }, [countPage]);

  const listCreator = () => {
    var countForTrSplit = 1;
    var trWebtoonList = [];

    return (
      <>
        {firstLoading ? (
          <div id ='firstLoading'>
            <FaSpinner className="loadingIcon" />
          </div>
        ) : (
          <>
            <table>
              {webtoonList
                .filter((webtoonInfo) => webtoonInfo.title.includes(searchTitle))
                .map((webtoonInfo) => {
                  if (countForTrSplit === 1) {
                    trWebtoonList = [];
                  }

                  trWebtoonList.push(webtoonInfo);
                  if (countForTrSplit === 4) {
                    countForTrSplit = 1;
                    return (
                      <tr>
                        {trWebtoonList.map((trWebtoonInfo) => (
                          <td>
                            <tr>
                              <td>
                                <button
                                  onClick={() => {
                                    navigate('/mainwebtooninfo', { state: { mastrId: trWebtoonInfo.mastrId } });
                                  }}
                                >
                                  <img src={trWebtoonInfo.imageDownloadUrl} alt="image error" />
                                </button>
                              </td>
                            </tr>
                            <tr style={{ height: '65px' }}>
                              <td style={{ height: '75px' }}>
                                <p className="webtoonTitle">
                                  {trWebtoonInfo.title}
                                </p>
                              </td>
                            </tr>
                          </td>
                        ))}
                      </tr>
                    );
                  } else {
                    countForTrSplit += 1;
                  }
                })}
            </table>

            {isLoading ? ( // 로딩 중일 때의 화면
              <div id = 'isLoading'>
                <FaSpinner className="loadingIcon" />
              </div>
            ) : (
              <div></div>
            )}
          </>
        )}
      </>
    );
  };

  return (
    <>
      <MainBackgorund>
        <MainBackSmall>
        </MainBackSmall>
          <div className="webSearch">
          <input
            id="mainSearchInput"
            type="text"
            placeholder="찾고싶은 웹툰 검색!"
            value={webtoontitle}
            onChange={(e) => setWebtoontitle(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                handleSearch();
              }
            }}
          />
            {listCreator()}
          </div>

          {showNavigationToScrollTop ? (
            <div id='showNavigationToScrollTop'
              onClick={() => {
                window.scrollTo({ top: 0, behavior: 'smooth' });
              }}
            >
              <img src={toTop} alt="To Top" style={{ height: '100%', width: '100%' }} />
            </div>
          ) : null}
      </MainBackgorund>
    </>
  );
}

export default Search;
