import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../images/logoimage.png';
import './backGround.module.css';
import axios from 'axios';

function MainBackgorund(props) {
  const navigate = useNavigate();
  const [loggedUserName, setLoggedUserName] = useState('지금 로그인하세요!');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/toonder/webtoon?p_num=${searchQuery}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error(error);
    }
  };




  return (
    <div className="mainPageBack">
      <article>
        <div className="logoSearch">
          <div className="mainLogo">
            <button
              id="main_page"
              onClick={() => {
                navigate('/main_page');
              }}
            >
              <img src={logo} width="84px" height="93px" alt="image error" />
              <h1>Toonder</h1>
            </button>
          </div>
          <div className="mainSearch">
            <input
              id="mainSearchInput"
              type="text"
              placeholder="찾고싶은 웹툰 검색!"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button id="mainSearchButton" type='submit' onClick={handleSearch}>검색</button>
          </div>
        </div>
        {/* 검색 결과 렌더링 */}
        {searchResults.length > 0 && (
          <div className="searchResults">
            <h2>검색 결과:</h2>
            <ul>
              {searchResults.map((result) => (
                <li key={result.id}>{result.title}</li>
              ))}
            </ul>
          </div>
        )}
        {props.children}
      </article>
      <footer className="footer"> {/* 수정 */}
      </footer>
    </div>
  );
}

export default MainBackgorund;
