// MainBackSmall.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './backGround.module.css';
import ex1 from '../../images/ex1.png';
import axios from 'axios';
import supabase from '../supabase';
import Modal from './Modal'; // Import the Modal component

function MainBackSmall(props) {
  const navigate = useNavigate();
  const [loggedUserName, setLoggedUserName] = useState(
    localStorage.getItem('loggedUserName')
  );
  const [loggedUserImage, setLoggedUserImage] = useState(
    localStorage.getItem('loggedUserPhoto') || ex1
  );
  const [modalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await supabase.auth.getSession();
      const email = data.session.user.email;
      const requestData = {
        email: email,
      };

      if (!localStorage.getItem('loggedUserName')) {
        axios
          .post('toonder/name', requestData)
          .then((loggedUserData) => {
            console.log(loggedUserData.data.mem_name);
            setLoggedUserName(loggedUserData.data.mem_name);
            localStorage.setItem(
              'loggedUserName',
              loggedUserData.data.mem_name
            );
          })
          .catch((error) => console.log(error));
      }

      if (!localStorage.getItem('loggedUserPhoto')) {
        axios
          .post('toonder/photo', requestData)
          .then((loggedUserData) => {
            console.log(loggedUserData.data.mem_photo);
            setLoggedUserImage(loggedUserData.data.mem_photo);
            localStorage.setItem(
              'loggedUserPhoto',
              loggedUserData.data.mem_photo
            );
          })
          .catch((error) => console.log(error));
      }
    };

    fetchData();
  }, []);

  return (
    <div className="mainBackSmall">
      <div className="mainInfo">
        <div className="mainInfoImg">
          <button id="mainInfoButton" onClick={openModal}>
            <img src={loggedUserImage} alt="image error" />
          </button>
          <span>{loggedUserName}님, 안녕하세요!</span>
          <Modal open={modalOpen} close={closeModal} />
        </div>
        <div className="searchArea">
          <input
            id="mainSearchInput"
            type="text"
            placeholder="찾고싶은 웹툰 검색!"
            value={props.searchTitle}
            onChange={(e) => props.setSearchTitle(e.target.value)}
          />
          <button
            id="mainSearchButtonSmall"
            onClick={() => {
              navigate(`/search?title=${props.searchTitle}`);
            }}
          >
            검색
          </button>
        </div>
      </div>
    </div>
  );
}

export default MainBackSmall;
